public class Ex006 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int val1 = 7;
		int val2 = 3;

	System.out.println("7+3=" + (val1 + val2));
	System.out.println("7-3=" + (val1 - val2));
	System.out.println("7×3=" + (val1 * val2));
	System.out.println("7÷3=" + (val1 / val2));
	System.out.println("7%3=" + (val1 % val2));

	}

}
